import React, { useState } from "react";
import { HiMenuAlt1, HiX } from "react-icons/hi";

const SideNavbar = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 w-64 transition-all duration-300 ease-in-out ${
          isSidebarOpen ? "block" : "hidden"
        }`}
      >
        <div className="flex items-center justify-center mt-8">
          <img
            className="w-auto rounded-full h-12"
            style={{ height: "80px", boxShadow: "2px 2px 200px white" }}
            src="https://drive.google.com/uc?export=view&id=1Ho2t6tuc6hPaMSc1lviu9lpZAQJw6Tn0"
            alt=""
          />
          <span className="text-2xl font-semibold ml-2">My App</span>
        </div>
        <nav className="mt-10">
          <a
            href="#"
            className="block py-2.5 px-4 text-gray-600 hover:bg-gray-100 hover:text-gray-800"
          >
            Dashboard
          </a>
          {/* add more links here */}
        </nav>
      </div>
      {/* Content */}
      <div
        className={`flex-1 overflow-auto ${
          isSidebarOpen ? "ml-64" : "ml-0"
        } transition-all duration-300 ease-in-out`}
      >
        <div className="p-10">
          {/* Toggle button */}
          <button
            className="absolute top-6 right-6 bg-gray-100 rounded-full p-2 focus:outline-none"
            onClick={toggleSidebar}
          >
            {isSidebarOpen ? (
              <HiX className="text-2xl text-gray-600" />
            ) : (
              <HiMenuAlt1 className="text-2xl text-gray-600" />
            )}
          </button>
          {/* Content */}
          <h1 className="text-3xl font-bold mb-5">Welcome to My App</h1>
          <p className="text-lg">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>
        </div>
      </div>
    </div>
  );
};

export default SideNavbar;
