import React from 'react'

const TutorialContent = () => {
    const location = useLocation();
    const name = location.state && location.state.name;
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    const toggleSidebar = () => {
        setIsSidebarOpen(!isSidebarOpen);
    };
    // const { richTextFieldContent } = props;

    useEffect(() => {

        async function fetchData() {
            try {
                fetch(`http://127.0.0.1:8000/Tutorials/ReadTutorials/${name}/`)
                    .then((response) => response.json())
                    .then((json) => {
                        console.log("JSON: ", json)
                        let data = JSON.parse(json)
                        setProducts(data)
                        console.log("response", data)

                        setLoading(false);
                    })
            }
            catch (error) {
                console.log("ERROR LOGS: ", error);
            }
            // }, 10000);
        }

        fetchData();
    }, []);
    return (
        <div>TutorialContent
            <div className="container px-4 " style={{ paddingTop: "95px" }}>
                {products && products.map((record, index) => (
                    <div className="max-w-4xl mx-auto">
                        {record.fields.title}
                        <div className="prose prose-lg">
                            <div dangerouslySetInnerHTML={{ __html: record.fields.content }}></div>
                        </div>
                    </div>
                ))
                }
            </div>
        </div>
    )
}

export default TutorialContent
