import React, { useEffect, useState } from 'react';
import Header from '../Header';
import Footer from '../Footer';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const ReadTutorials = () => {
  const location = useLocation();
  const name = location.state && location.state.name;
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  // const { richTextFieldContent } = props;

  useEffect(() => {

    async function fetchData() {
      try {
        fetch(`http://127.0.0.1:8000/Tutorials/ReadTutorials/${name}/`)
          .then((response) => response.json())
          .then((json) => {
            console.log("JSON: ", json)
            let data = JSON.parse(json)
            setProducts(data)
            console.log("response", data)

            setLoading(false);
          })
      }
      catch (error) {
        console.log("ERROR LOGS: ", error);
      }
      // }, 10000);
    }

    fetchData();
  }, []);

  return (
    <div>
      <Header />
      {/* <div className="container px-4 " style={{ paddingTop: "95px" }}>
                {products && products.map((record, index) => (
                    <div className="max-w-4xl mx-auto">
                      {record.fields.title}
                        <div className="prose prose-lg">
                            <div dangerouslySetInnerHTML={{ __html: record.fields.content }}></div>
                        </div>
                    </div>
                ))
                }
            </div> */}
      <div className="container mx-auto py-10">
        <div className="flex flex-wrap">
          <div className="w-full md:w-1/4">
            <ul className="border-r-2 border-gray-300 py-12">
              <li className="py-2 px-4 text-gray-600 hover:text-gray-900 font-medium">
                <a href="#tutorial-1">Tutorial 1</a>
              </li>
              <li className="py-2 px-4 text-gray-600 hover:text-gray-900 font-medium">
                <a href="#tutorial-2">Tutorial 2</a>
              </li>
              <li className="py-2 px-4 text-gray-600 hover:text-gray-900 font-medium">
                <a href="#tutorial-3">Tutorial 3</a>
              </li>
              <li className="py-2 px-4 text-gray-600 hover:text-gray-900 font-medium">
                <a href="#tutorial-4">Tutorial 4</a>
              </li>
            </ul>
          </div>
          <div className="w-full md:w-3/4">
            <div id="tutorial-1" className="p-6">
              <h2 className="text-2xl font-medium mb-4">Tutorial 1</h2>
              <p className="text-gray-600 leading-7">
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris euismod lorem a nisl ullamcorper, ac feugiat augue mattis. Proin eu ante aliquet, vestibulum turpis eget, semper tellus.
              </p>
            </div>
            <div id="tutorial-2" className="p-6">
              <h2 className="text-2xl font-medium mb-4">Tutorial 2</h2>
              <p className="text-gray-600 leading-7">
                Sed malesuada libero ac nibh auctor rhoncus. Nulla suscipit odio sit amet justo elementum, id bibendum velit euismod. Ut tincidunt vel velit in varius.
              </p>
            </div>
            <div id="tutorial-3" className="p-6">
              <h2 className="text-2xl font-medium mb-4">Tutorial 3</h2>
              <p className="text-gray-600 leading-7">
                Vestibulum facilisis orci ac libero ullamcorper, a elementum ante molestie. In aliquam, turpis ac malesuada finibus, elit orci sollicitudin est, ut efficitur or</p>
            </div>
            <div id="tutorial-4" className="p-6">
              <h2 className="text-2xl font-medium mb-4">Tutorial 4</h2>
              <p className="text-gray-600 leading-7">
                Praesent accumsan velit id vestibulum blandit. Maecenas et sodales nulla, ut tempor mauris. Aenean sed lorem vel metus aliquam sagittis non non mauris.
              </p>
            </div>
          </div>
        </div>
      </div>


      <Footer />

    </div >
  )
}

export default ReadTutorials
