              // >
              //   {record.fields.title}
              // </a>
              <li key={record.pk}
              className={selectItem === index ? "block py-2.5 px-4 text-gray-100" : "block py-2.5 px-4 text-gray-800"} 
              onClick={() => {setSelectItem(index)}}
