import React, { useEffect, useState } from 'react';
import Header from '../Header';
import { Link } from 'react-router-dom';

const ReadBlog = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    // const { richTextFieldContent } = props;

    useEffect(() => {

        async function fetchData() {
            try {
                fetch('http://127.0.0.1:8000/Blogs/ReadBlog/:title/')
                    .then((response) => response.json())
                    .then((json) => {
                        console.log("JSON: ", json)
                        let data = JSON.parse(json)
                        setProducts(data)
                        console.log("response", data)

                        setLoading(false);
                    })
            }
            catch (error) {
                console.log("ERROR LOGS: ", error);
            }
            // }, 10000);
        }

        fetchData();
    }, []);

    return (
        <div>
            {/* <Header /> */}
            {/* <AllBlogs /> */}
            <h1 className='text-3xl font-medium title-font text-gray-900 text-center my-12'>Blogs</h1>
            <section className="text-gray-600  body-font">
                <div className="container px-5 mx-auto">
                    <div className="flex flex-wrap -m-4">
                        {products && products.map((record, index) => (
                            <div className="p-4 md:w-1/3" key={record.id}>
                                <div className="h-full border-2 border-gray-200 border-opacity-60 rounded-lg overflow-hidden">
                                    <img className="lg:h-48 md:h-36 w-full object-cover object-center" src={`http://localhost:8000/media/${record.fields.img}`} alt="blog" />
                                    <div className="p-6">
                                        <h2 className="tracking-widest text-xs title-font font-medium text-gray-400 mb-1"></h2>
                                        <h1 className="title-font text-lg font-medium text-gray-900 mb-3">{record.fields.title}</h1>
                                        <p className="leading-relaxed mb-3">{record.fields.content.slice(41, 160)}</p>
                                        {/* <ReactQuill value={record.fields.content.slice(0, 80)} readOnly={true} /> */}
                                        <div className="flex items-center flex-wrap ">
                                            {/* <a className="text-indigo-500 inline-flex items-center md:mb-2 lg:mb-0">Learn More

                                        </a> */}
                                            {/* <Link to={`/Blogs/ReadBlog/${record.fields.id}`}>Learn More</Link> */}
                                            <Link to={`/Blogs/ReadBlog/${record.fields.title}`}>Read More</Link>{record.fields.id}sdf
                                            <span className="text-gray-400 mr-3 inline-flex items-center lg:ml-auto md:ml-0 ml-auto leading-none text-sm pr-3 py-1 border-r-2 border-gray-200">

                                            </span>
                                            <span className="text-gray-400 inline-flex items-center leading-none text-sm">

                                            </span>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </section>
            {/* <Footer /> */}
        </div>
    )
}

export default ReadBlog
