import React, { useEffect, useState } from 'react';
import Header from '../Header'
import axios from 'axios';
import { Link } from 'react-router-dom';

const Login = () => {
    function getCookie(name) {
        const value = `; ${document.cookie}`;
        const parts = value.split(`; ${name}=`);
        if (parts.length === 2) return parts.pop().split(";").shift();
    }
    const csrfToken = getCookie("csrftoken");

    const [formData, setFormData] = useState({
        name: "",
        email: "",
        username: "",
        password1: "",
        password2: "",
    });
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [username, setUsername] = useState('');
    const [password1, setPassword1] = useState('');
    const [password2, setPassword2] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("Name: ", name)
        console.log("email: ", email)
        console.log("user: ", username)
        console.log("password1: ", password1)
        console.log("password2: ", password2)

        try {
            const response = await axios.post('/SignUp/', {
                headers: {
                    "Content-Type": "application/json",
                    "X-CSRFToken": csrfToken,
                },
                body: JSON.stringify({ name, email, username, password1, password2 }),
            });

            if (response.ok) {
                console.log('Data Saved');
            } else {
                throw new Error('Something went wrong');
            }
        } catch (error) {
            console.error(error);
        }
    };


    return (
        <div>
            <Header />
            <div class="max-w-md mx-auto bg-gray-100 p-6 my-12 rounded-lg shadow-md">
                <h2 class="text-2xl font-bold mb-6 my-12">Log In</h2>
                <form>
                    <div class="mb-4">
                        <label class="block font-medium mb-2" for="email">Email</label>
                        <input class="w-full px-4 py-2 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent" type="email" id="email" name="email" required />
                    </div>
                    <div class="mb-4">
                        <label class="block font-medium mb-2" for="password">Password</label>
                        <input class="w-full px-4 py-2 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent" type="password" id="password" name="password" required />
                    </div>
                    <button class="w-full bg-blue-500 hover:bg-blue-600 text-white font-medium py-2 rounded-lg shadow-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2" type="submit">Log In</button>
                </form>
                <p class="text-gray-500 text-sm mt-4">Don't have an account? <a class="text-blue-500 hover:text-blue-600 underline" href="#">Sign up now</a>.</p>
            </div>
        </div>

    )
}

export default Login
