import React, { useEffect, useState } from 'react';
import Header from '../Header';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';

const ReadBlog = () => {
    const location = useLocation();
    const name = location.state && location.state.name;
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    // const { richTextFieldContent } = props;

    useEffect(() => {

        async function fetchData() {
            try {
                fetch(`http://127.0.0.1:8000/Blogs/ReadBlog/${name}/`)
                    .then((response) => response.json())
                    .then((json) => {
                        console.log("JSON: ", json)
                        let data = JSON.parse(json)
                        setProducts(data)
                        console.log("response", data)

                        setLoading(false);
                    })
            }
            catch (error) {
                console.log("ERROR LOGS: ", error);
            }
            // }, 10000);
        }

        fetchData();
    }, []);

    return (
        <div>
            {name}
            {/* {name.pub_date} */}
            {products && products.map((record, index) => (
                <div className="p-4 md:w-1/3" key={record.id}>
                    <div className="h-full border-2 border-gray-200 border-opacity-60 rounded-lg overflow-hidden">
                        <img className="lg:h-48 md:h-36 w-full object-cover object-center" src={`http://localhost:8000/media/${record.fields.img}`} alt="blog" />
                        <div className="p-6">
                            <h2 className="tracking-widest text-xs title-font font-medium text-gray-400 mb-1"></h2>
                            <h1 className="title-font text-lg font-medium text-gray-900 mb-3">{record.fields.title}</h1>
                            <p className="leading-relaxed mb-3">{record.fields.content}</p>
                            {/* <ReactQuill value={record.fields.content.slice(0, 80)} readOnly={true} /> */}
                            <div className="flex items-center flex-wrap ">
                                                              <div className="max-w-3xl mx-auto">
                                        <div className="prose prose-lg">
                                            <div dangerouslySetInnerHTML={{ __html: record.fields.content }}></div>
                                        </div>
                                    </div>
                                    {/* <div dangerouslySetInnerHTML={{ __html: record.fields.content }} style={{color: "black", background: "transparent", border:"none"}}></div> */}
                                {/* </span> */}
                                <span className="text-gray-400 inline-flex items-center leading-none text-sm">

                                </span>
                            </div>
                        </div>
                    </div>
                </div>
            ))}

        </div>
    )
}

export default ReadBlog
